import heapq as hq
from typing import Union
def heap_queue_largest(nums: list[Union[int, float]], n: int) -> list[Union[int, float]]:
  if not isinstance(nums, list):
      raise TypeError('Input nums must be a list of real numbers.')
  if not all([isinstance(i, (int, float)) for i in nums]):
      raise TypeError('Input nums must be a list of real numbers.')
  if not isinstance(n, int):
      raise TypeError('Input n must be a positive integer.')
  if n <= 0:
      raise ValueError('Input n must be a positive integer.')
  largest_nums = hq.nlargest(n, nums)
  return largest_nums

