from typing import Union
def unique(l: Union[list[Union[int, float]], list[str]]) -> Union[list[Union[int, float]], list[str]]:
    if (not isinstance(l, list)):
      raise TypeError('Input l must be a list of elements that are all strings or all real numbers.')
    if not (all([(isinstance(i, float) or isinstance(i, int)) for i in l]) or all([isinstance(i, str) for i in l])):
      raise TypeError('Input l must be a list of elements that are all strings or all real numbers.')
    return sorted(list(set(l)))

